properties.body:
{
  "type": "runtime_user.email_credentials",
  "base_type": "runtime_user",
  "name": "SXO Email Automation Account",
  "title": "SXO Email Automation Account",
  "properties": {
    "display_name": "SXO Email Automation Account",
    "sender": "$workflow.definition_workflow_01MCQVAMBNUS95F1zToedd5oU9CSmvGyOdc.local.variable_workflow_01VBK4050ET5B3EO6S2wTWeon6nvrfQEfwW$",
    "password": "$workflow.definition_workflow_01MCQVAMBNUS95F1zToedd5oU9CSmvGyOdc.local.variable_workflow_01VBK4DBTBAX85uCeXFitsLwnuiLLf6KXnv$",
    "description": "This Accountkeys created through the Email Events Automation workflow"
  }
}

{
  "title": "SXO EMAIL Automation Event Acount",
  "target_id": "$activity.definition_activity_01MCQVBRPK6XA6ZU7w1bzCps93UyL02h18C.output.jsonpath_queries.IMAP Target ID$",
  "type": "email.event",
  "base_type": "event",
  "properties": {
    "folder": "INBOX",
    "action": "read",
    "download_attachments": true,
    "title": "SXO EMAIL Automation Event Acount",
    "target_id": "$activity.definition_activity_01MCQVBRPK6XA6ZU7w1bzCps93UyL02h18C.output.jsonpath_queries.IMAP Target ID$",
    "conditions": {
      "operator": "eqi",
      "left_operand": "$output.subject$",
      "right_operand": "SXO EMAIL Explicit Stress$workflow.definition_workflow_01MCQVAMBNUS95F1zToedd5oU9CSmvGyOdc.output.instance_id$"
    }
  }
}

{
  "name": "Email trigger Automatiom WF",
  "title": "Email trigger Automatiom WF",
  "type": "generic.workflow",
  "base_type": "workflow",
  "properties": {
    "display_name": "Email trigger Automatiom WF",
    "atomic": {
      "is_atomic": false
    },
    "delete_workflow_instance": false,
    "runtime_user": {
      "override_target_runtime_user": false,
      "specify_on_workflow_start": false,
      "target_default": true
    },
    "target": {
      "execute_on_target_group": false,
      "execute_on_workflow_target": false,
      "no_target": true,
      "specify_on_workflow_start": false
    }
  }
}

{
  "previous_action": {
    "id": "$activity.definition_activity_01MCQVD34EVN66Jdm0sO4KxQIdYkLJPGzBL.output.jsonpath_queries.Worklfow ID$",
    "type": "generic.workflow",
    "base_type": "workflow"
  },
  "action": {
    "type": "core.string2upper",
    "base_type": "activity",
    "name": "To Upper",
    "properties": {
      "continue_on_failure": false,
      "skip_execution": false,
      "display_name": "To Upper",
      "source": "automation"
    },
    "title": "To Upper"
  }
}

{
  "name": "Send Email Automation WF",
  "title": "Send Email Automation WF",
  "type": "generic.workflow",
  "base_type": "workflow",
  "properties": {
    "display_name": "Send Email Automation WF",
    "atomic": {
      "is_atomic": false
    },
    "delete_workflow_instance": false,
    "runtime_user": {
      "override_target_runtime_user": false,
      "specify_on_workflow_start": false,
      "target_default": true
    },
    "target": {
      "execute_on_target_group": false,
      "execute_on_workflow_target": false,
      "no_target": true,
      "specify_on_workflow_start": false
    }
  }
}

{
  "previous_action": {
    "id": "$activity.definition_activity_01MCQVFB1N60T5NCTdbQwzPgc7DtSnxfLbI.output.jsonpath_queries.Worklfow ID$",
    "type": "generic.workflow",
    "base_type": "workflow"
  },
  "action": {
    "type": "email.smtp_send_email",
    "base_type": "activity",
    "name": "Send Email",
    "properties": {
      "action_timeout": 180,
      "continue_if_one_address_valid": false,
      "continue_on_failure": false,
      "runtime_user": {
        "override_target_runtime_user": false,
        "target_default": true
      },
      "skip_execution": false,
      "target": {
        "override_workflow_target": true,
        "target_id": "$activity.definition_activity_01MCQVBHW33IJ2Sa7SQ6bRWswwLENKw17WI.output.jsonpath_queries.SMTP Targt ID$"
      },
      "display_name": "Send Email"
    },
    "title": "Send Email"
  }
}

{
  "id": "$activity.definition_activity_01MCQVFL1H0WY37zlQmsu8B2U7NBlP981yf.output.jsonpath_queries.Email Action ID$",
  "title": "Send Email",
  "properties": {
    "display_name": "Send Email",
    "action_timeout": 180,
    "continue_if_one_address_valid": false,
    "continue_on_failure": false,
    "runtime_user": {
      "override_target_runtime_user": false,
      "target_default": true
    },
    "skip_execution": false,
    "target": {
      "override_workflow_target": true,
      "target_id": "$activity.definition_activity_01MCQVBHW33IJ2Sa7SQ6bRWswwLENKw17WI.output.jsonpath_queries.SMTP Targt ID$"
    },
    "to": "$workflow.definition_workflow_01MCQVAMBNUS95F1zToedd5oU9CSmvGyOdc.input.variable_workflow_01MCQVALSPD305Nyt4gACOcHnRRUwlee8af$",
    "subject": "SXO EMAIL Explicit Stress$workflow.definition_workflow_01MCQVAMBNUS95F1zToedd5oU9CSmvGyOdc.output.instance_id$",
    "body": "Testing Email events using Automation Workflows"
  }
}

{
  "type": "webhook.event",
  "base_type": "event",
  "title": "WH_JSON_Event",
  "description": "Automation for Webhook JSON Event",
  "properties": {
    "display_name": "WH_JSON_Event",
    "title": "WH_JSON_Event",
    "webhook_id": "$activity.definition_activity_01VBV2KJH7FN15gMVUO41M8XS5lOeN11RTE.output.jsonpath_queries.webhook_id$",
    "description": "Automation for Webhook JSON Event",
    "conditions":{"operator":"mregex","left_operand":"[$output.request_body$]","right_operand":"[:](\\\"test\\\")"}
  }
}
