        "base_type": "activity",
        "properties": {
          "action_timeout": 180,
          "continue_on_failure": false,
          "display_name": "Generate ticket JSON",
          "script": "import sys,json\n\ndescription = sys.argv[1]\nshort_description = sys.argv[2]\nwork_notes = sys.argv[3]\nimpact = sys.argv[4]\nurgency = sys.argv[5]\ncaller_id = sys.argv[6]\n\nticket_object = {}\nticket_object[\"description\"] = description\nticket_object[\"short_description\"] = short_description\nif len(work_notes) > 0:ticket_object[\"work_notes\"] = work_notes\nticket_object[\"urgency\"] = urgency\nticket_object[\"impact\"] = impact\nticket_object[\"caller_id\"] = caller_id\n\nticket_json = json.dumps(ticket_object)\n",
          "script_arguments": [
            "$workflow.definition_workflow_01C0BYD0GI1KZ0mxScCSxVrfX70zrUdqLlW.input.variable_workflow_01FGYF0DUR8HV2TMpEKctsYjQR4mzjD5OTu$",
